mapper.select:
SELECT * FROM total_diet


		account_no = #{accountNo}				


		AND member_no = #{memberNo}				

		AND save_date BETWEEN SYSDATE-7 and SYSDATE

select TRUNC(AVG(kcal),1) kcal, TRUNC(AVG(water),1) water, TRUNC(AVG(protein),1) protein, TRUNC(AVG(fat),1) fat, TRUNC(AVG(batch),1) batch, TRUNC(AVG(carbohydrate),1) carbohydrate, TRUNC(AVG(sugars),1) sugars, TRUNC(AVG(dietary_fiber),1) dietary_fiber, TRUNC(AVG(calcium),1) calcium, TRUNC(AVG(iron_content),1) iron_content, TRUNC(AVG(phosphorus),1) phosphorus, TRUNC(AVG(potassium),1) potassium, TRUNC(AVG(sodium),1) sodium, TRUNC(AVG(vitamin_a),1) vitamin_a, TRUNC(AVG(retinol),1) retinol, TRUNC(AVG(beta_carotene),1) beta_carotene, TRUNC(AVG(thiamine),1) thiamine, TRUNC(AVG(riboflavin),1) riboflavin, TRUNC(AVG(niacin),1) niacin, TRUNC(AVG(vitamin_c),1) vitamin_c, TRUNC(AVG(vitamin_d),1) vitamin_d, TRUNC(AVG(cholesterol),1) cholesterol, TRUNC(AVG(saturated_fat),1) saturated_fat, TRUNC(AVG(trans_fat),1) trans_fat from (SELECT SUM(kcal) kcal, SUM(water) water, SUM(protein) protein, SUM(fat) fat, SUM(batch) batch, SUM(carbohydrate) carbohydrate, SUM(sugars) sugars, SUM(dietary_fiber) dietary_fiber, SUM(calcium) calcium, SUM(iron_content) iron_content, SUM(phosphorus) phosphorus, SUM(potassium) potassium, SUM(sodium) sodium, SUM(vitamin_a) vitamin_a, SUM(retinol) retinol, SUM(beta_carotene) beta_carotene, SUM(thiamine) thiamine, SUM(riboflavin) riboflavin, SUM(niacin) niacin, SUM(vitamin_c) vitamin_c, SUM(vitamin_d) vitamin_d, SUM(cholesterol) cholesterol, SUM(saturated_fat) saturated_fat, SUM(trans_fat) trans_fat FROM total_diet
account_no = #{accountNo}
AND member_no = #{memberNo}
AND save_date BETWEEN SYSDATE-7 and SYSDATE GROUP BY save_date)

SELECT TO_CHAR(save_date, 'YYYY-MM-DD') save_date, SUM(kcal) kcal, SUM(water) water, SUM(protein) protein, SUM(fat) fat, SUM(batch) batch, SUM(carbohydrate) carbohydrate, SUM(sugars) sugars, SUM(dietary_fiber) dietary_fiber, SUM(calcium) calcium, SUM(iron_content) iron_content, SUM(phosphorus) phosphorus, SUM(potassium) potassium, SUM(sodium) sodium, SUM(vitamin_a) vitamin_a, SUM(retinol) retinol, SUM(beta_carotene) beta_carotene, SUM(thiamine) thiamine, SUM(riboflavin) riboflavin, SUM(niacin) niacin, SUM(vitamin_c) vitamin_c, SUM(vitamin_d) vitamin_d, SUM(cholesterol) cholesterol, SUM(saturated_fat) saturated_fat, SUM(trans_fat) trans_fat FROM total_diet
account_no = #{accountNo}
AND member_no = #{memberNo}
AND save_date BETWEEN SYSDATE-30 and SYSDATE GROUP BY save_date ORDER BY save_date ASC

SELECT td.log_no log_no, td.account_no account_no, td.member_no member_no, TO_CHAR(td.save_date, 'YYYY-MM-DD') save_date, td.food_code food_code, ft.food_name food_name, td.kcal kcal, td.water water, td.protein protein, td.fat fat, td.batch batch, td.carbohydrate carbohydrate, td.sugars sugars, td.dietary_fiber dietary_fiber, td.calcium calcium, td.iron_content iron_content, td.phosphorus phosphorus, td.potassium potassium, td.sodium sodium, td.vitamin_a vitamin_a, td.retinol retinol, td.beta_carotene beta_carotene, td.thiamine thiamine, td.riboflavin riboflavin, td.niacin niacin, td.vitamin_c vitamin_c, td.vitamin_d vitamin_d, td.cholesterol cholesterol, td.saturated_fat saturated_fat, td.trans_fat trans_fat FROM ( SELECT * FROM total_diet
account_no = #{accountNo}
AND member_no = #{memberNo}
AND save_date BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')
) td, ( SELECT food_code, food_name, kcal, water, protein, fat, batch, carbohydrate, sugars, dietary_fiber, calcium, iron_content, phosphorus, potassium, sodium, vitamin_a, retinol, beta_carotene, thiamine, riboflavin, niacin, vitamin_c, vitamin_d, cholesterol, saturated_fat, trans_fat FROM food_list_processed UNION ALL SELECT food_code, food_name, kcal, water, protein, fat, batch, carbohydrate, sugars, dietary_fiber, calcium, iron_content, phosphorus, potassium, sodium, vitamin_a, retinol, beta_carotene, thiamine, riboflavin, niacin, vitamin_c, vitamin_d, cholesterol, saturated_fat, trans_fat FROM food_list_cooked UNION ALL SELECT food_code, food_name, kcal, water, protein, fat, batch, carbohydrate, sugars, dietary_fiber, calcium, iron_content, phosphorus, potassium, sodium, vitamin_a, retinol, beta_carotene, thiamine, riboflavin, niacin, vitamin_c, vitamin_d, cholesterol, saturated_fat, trans_fat FROM food_list_agriculture UNION ALL SELECT food_code, food_name, kcal, water, protein, fat, batch, carbohydrate, sugars, dietary_fiber, calcium, iron_content, phosphorus, potassium, sodium, vitamin_a, retinol, beta_carotene, thiamine, riboflavin, niacin, vitamin_c, vitamin_d, cholesterol, saturated_fat, trans_fat FROM food_list_fisheries ) ft
td.food_code = ft.food_code
ORDER BY td.save_date DESC

SELECT COUNT(*) foodName, TO_CHAR(save_date, 'YYYY-MM-DD') save_date, SUM(kcal) kcal, SUM(water) water, SUM(protein) protein, SUM(fat) fat, SUM(batch) batch, SUM(carbohydrate) carbohydrate, SUM(sugars) sugars, SUM(dietary_fiber) dietary_fiber, SUM(calcium) calcium, SUM(iron_content) iron_content, SUM(phosphorus) phosphorus, SUM(potassium) potassium, SUM(sodium) sodium, SUM(vitamin_a) vitamin_a, SUM(retinol) retinol, SUM(beta_carotene) beta_carotene, SUM(thiamine) thiamine, SUM(riboflavin) riboflavin, SUM(niacin) niacin, SUM(vitamin_c) vitamin_c, SUM(vitamin_d) vitamin_d, SUM(cholesterol) cholesterol, SUM(saturated_fat) saturated_fat, SUM(trans_fat) trans_fat FROM total_diet
account_no = #{accountNo}
AND member_no = #{memberNo}
AND save_date BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')
GROUP BY save_date ORDER BY save_date DESC
