mapper.select:
SELECT account_no, member_no, email, pw, name, nickname, tel, TO_CHAR(birth, 'YYMMDD') birth, gender_id, created_at, icon_id
FROM member
WHERE account_no = #{accountNo} and member_no = #{memberNo}

SELECT account_no, member_no, email, pw, name, nickname, tel, TO_CHAR(birth, 'YYMMDD') birth, gender_id, created_at, icon_id 
FROM member
WHERE email = #{email}

        SELECT count(*) 
		FROM member
		WHERE account_no = #{accountNo}

SELECT CEIL(MONTHS_BETWEEN(SYSDATE, birth) / 12)
FROM member
WHERE account_no = #{accountNo} and member_no = #{memberNo}

        SELECT gender_name
		FROM gender
		WHERE gender_id = #{genderId}

        SELECT COUNT(*)
		  FROM member
		 WHERE email = #{email}
